const log4js = require('log4js')

log4js.configure({
  appenders: { app: { type: 'file', filename: 'logs/app.log', backup: 7 } },
  categories: { default: { appenders: ['app'], level: 'debug' } }
});



module.exports = function(name) {
	name = name.substring(name.indexOf('news-wall/')+10);

	var logger = log4js.getLogger(name);
	logger.level = 'debug';
	return logger;
}
